mapper.select:
SELECT f.departure_date, f.flight_name, f.fare_type_cd, f.vacant_num, f.boarding_class_cd FROM flight f, fare_type ft, flight_master fm, route r WHERE f.fare_type_cd = ft.fare_type_cd AND f.fare_type_cd IN
#{fareType}
AND f.flight_name = fm.flight_name AND fm.route_no = r.route_no AND r.dep_airport_cd = #{criteria.route.departureAirport.code} AND r.arr_airport_cd = #{criteria.route.arrivalAirport.code} AND f.departure_date = #{criteria.depDate} AND f.boarding_class_cd = #{criteria.boardingClass} AND #{criteria.beforeDayNum} BETWEEN ft.rsrv_available_end_day_num AND ft.rsrv_available_start_day_num ORDER BY ft.display_order ASC, fm.departure_time ASC

mapper.update:
UPDATE
    flight
SET
    vacant_num = #{vacantNum}
WHERE
    departure_date = #{departureDate}
AND
    flight_name = #{flightMaster.flightName}
AND
    boarding_class_cd = #{boardingClass.boardingClassCd}
AND
    fare_type_cd = #{fareType.fareTypeCd}

mapper.select:
SELECT EXISTS (
    SELECT
        *
    FROM
        flight
    WHERE
        departure_date = #{departureDate}
    AND
        flight_name = #{flightName}
    AND
        boarding_class_cd = #{boardingClass.boardingClassCd}
    AND
        fare_type_cd = #{fareType.fareTypeCd}
    )
